import React from 'react'
import Botao from './Botao'

class Contador extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            segundos: 0,
            minutos: 0,
            stop: false,
            btnStop: "Pausar"
        }

    }

    incrementar(){
        this.setState (
            (state) => {
                if(state.segundos >= 59){
                    this.zerar()
                    this.incrementarMin()
                }
                return({segundos: state.segundos + 1})
            }
        )
    }

    zerar(){
        this.setState({segundos: 0})
    }

    incrementarMin(){
        this.setState({minutos: this.state.minutos +1})
    }

    componentDidMount(){
        this.timer = setInterval( () => this.incrementar(),1000)
    }

    zerarCronometro(){
        this.setState({
            segundos: 0,
            minutos: 0
        })
    }

    pausarCronometro(){
        this.setState({
            stop: !this.state.stop
        })
        if(this.state.stop)
        this.setState({
            btnStop: "Continuar"
        })
         else 
            this.setState({
                btnStop: "Pausar"
            })
    }

    render(){
        return (
            <div>
            <h1>{this.state.minutos}:{this.state.segundos}</h1>
            <Botao onClick={()=> this.zerarCronometro()} label="Zerar" />
            <Botao onClick={()=> this.pausarCronometro()} label={this.state.btnStop} />
            </div>
            
        )
    }
}

export default Contador
